import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import LoginPage from "./components/LoginPage";
import Navbar from "./components/Navbar";
import "./App.css";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState("");
  const [newDate, setNewDate] = useState("");
  const [addedBy, setAddedBy] = useState("");
  const [eventDescription, setEventDescription] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchCriteria, setSearchCriteria] = useState("eventName");
  const [sortCriteria, setSortCriteria] = useState("date");

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };

  const onLogin = async (username, password) => {
    try {
      const response = await fetch("http://localhost:5001/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      if (response.ok) {
        console.log("Login successful:", data);
        localStorage.setItem("token", data.token);
        setIsLoggedIn(true);
        alert("Login successful!");
        fetchEvents();
      } else {
        alert(data.message || "Login failed");
      }
    } catch (error) {
      console.error("Error during login:", error);
      alert("An error occurred while logging in");
    }
  };

  const fetchEvents = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/events", {
        method: "GET",
        headers: {
          "x-auth-token": localStorage.getItem("token"),
        },
      });

      const data = await response.json();
      if (response.ok) {
        setEvents(data);
      } else {
        alert(data.message || "Failed to fetch events");
      }
    } catch (error) {
      console.error("Error fetching events:", error);
      alert("An error occurred while fetching events.");
    }
  };

  const handleAddEvent = async () => {
    if (newEvent && newDate && addedBy && eventDescription) {
      const event = {
        eventName: newEvent,
        date: newDate,
        addedBy,
        description: eventDescription,
      };

      try {
        const response = await fetch("http://localhost:5001/api/events/add", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "x-auth-token": localStorage.getItem("token"),
          },
          body: JSON.stringify(event),
        });

        const data = await response.json();
        if (response.ok) {
          setEvents((prevEvents) => [...prevEvents, data]);
          setNewEvent("");
          setNewDate("");
          setAddedBy("");
          setEventDescription("");
        } else {
          alert(data.message || "Failed to add event");
        }
      } catch (error) {
        console.error("Error while adding event:", error);
        alert("An error occurred while adding the event.");
      }
    } else {
      alert("Please fill out all fields.");
    }
  };

  const handleDeleteEvent = async (id) => {
    try {
      const response = await fetch(`http://localhost:5001/api/events/${id}`, {
        method: "DELETE",
        headers: {
          "x-auth-token": localStorage.getItem("token"),
        },
      });

      const data = await response.json();
      if (response.ok) {
        setEvents((prevEvents) =>
          prevEvents.filter((event) => event._id !== id)
        );
        alert(data.message || "Event deleted");
      } else {
        alert(data.message || "Failed to delete event");
      }
    } catch (error) {
      console.error("Error deleting event:", error);
      alert("An error occurred while deleting the event.");
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      fetchEvents();
    }
  }, [isLoggedIn]);

  // Filter and sort events
  const filteredEvents = events.filter((event) => {
    if (searchCriteria === "eventName") {
      return event.eventName.toLowerCase().includes(searchQuery.toLowerCase());
    } else if (searchCriteria === "date") {
      return formatDate(event.date).includes(searchQuery);
    }
    return true;
  });

  const sortedEvents = [...filteredEvents].sort((a, b) => {
    if (sortCriteria === "date") {
      return new Date(a.date) - new Date(b.date);
    } else if (sortCriteria === "eventName") {
      return a.eventName.localeCompare(b.eventName);
    }
    return 0;
  });

  if (!isLoggedIn) {
    return <LoginPage onLogin={onLogin} />;
  }

  return (
    <Router>
      <div className="app">
        <h1>Reminder Website</h1>
        <Navbar />

        <Routes>
          <Route
            path="/"
            element={
              <div className="events">
                <div className="controls">
                  <div className="sort-section">
                    <label>Sort by:</label>
                    <select
                      value={sortCriteria}
                      onChange={(e) => setSortCriteria(e.target.value)}
                    >
                      <option value="date">Date</option>
                      <option value="eventName">Event Name</option>
                    </select>
                  </div>
                  <div className="search-section">
                    <label>Search by:</label>
                    <select
                      value={searchCriteria}
                      onChange={(e) => setSearchCriteria(e.target.value)}
                    >
                      <option value="eventName">Event Name</option>
                      <option value="date">Date</option>
                    </select>
                    <input
                      type="text"
                      placeholder={`Search by ${searchCriteria}`}
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </div>
                </div>
                {sortedEvents.map((event) => (
                  <div
                    className={`event-card ${
                      event.addedBy === "Me" ? "my-event" : "friend-event"
                    }`}
                    key={event._id}
                  >
                    <div className="event-header">
                      <span className="event-date">
                        {formatDate(event.date)}
                      </span>
                      <span className="event-added-by">{event.addedBy}</span>
                    </div>
                    <h3>{event.eventName}</h3>
                    <p>{event.description}</p>
                    <button onClick={() => handleDeleteEvent(event._id)}>
                      Delete
                    </button>
                  </div>
                ))}
              </div>
            }
          />
          <Route
            path="/add-event"
            element={
              <div className="input-section">
                <input
                  type="text"
                  placeholder="Event Name"
                  value={newEvent}
                  onChange={(e) => setNewEvent(e.target.value)}
                />
                <input
                  type="date"
                  value={newDate}
                  onChange={(e) => setNewDate(e.target.value)}
                />
                <textarea
                  placeholder="Event Description"
                  value={eventDescription}
                  onChange={(e) => setEventDescription(e.target.value)}
                />
                <select
                  value={addedBy}
                  onChange={(e) => setAddedBy(e.target.value)}
                >
                  <option value="">Added By</option>
                  <option value="Me">Me</option>
                  <option value="Friend">Friend</option>
                </select>
                <button onClick={handleAddEvent}>Add Event</button>
              </div>
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
