import React, { useState } from "react";
import "./App.css";

function App() {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState("");
  const [newDate, setNewDate] = useState("");
  const [addedBy, setAddedBy] = useState("");

  const handleAddEvent = () => {
    if (newEvent && newDate && addedBy) {
      const event = { event: newEvent, date: newDate, addedBy };
      setEvents((prevEvents) => [...prevEvents, event]);
      setNewEvent("");
      setNewDate("");
      setAddedBy("");
    }
  };

  const handleDeleteEvent = (index) => {
    setEvents((prevEvents) => prevEvents.filter((_, i) => i !== index));
  };

  return (
    <div className="app">
      <h1>Reminder Website</h1>
      <div className="input-section">
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent}
          onChange={(e) => setNewEvent(e.target.value)}
        />
        <input
          type="date"
          value={newDate}
          onChange={(e) => setNewDate(e.target.value)}
        />
        <select value={addedBy} onChange={(e) => setAddedBy(e.target.value)}>
          <option value="">Added By</option>
          <option value="Me">Me</option>
          <option value="Friend">Friend</option>
        </select>
        <button onClick={handleAddEvent}>Add Event</button>
      </div>

      <div className="events">
        {events
          .sort((a, b) => new Date(a.date) - new Date(b.date))
          .map((event, index) => (
            <div
              className={`event-card ${
                event.addedBy === "Me" ? "my-event" : "friend-event"
              }`}
              key={index}
            >
              <h3>{event.event}</h3>
              <p>Date: {event.date}</p>
              <p>Added By: {event.addedBy}</p>
              <button onClick={() => handleDeleteEvent(index)}>Delete</button>
            </div>
          ))}
      </div>
    </div>
  );
}

export default App;
